import React, { Component } from "react";
import axios from "axios";

import "../styles/jukebox.css";

class Jukebox extends Component {
  constructor(props) {
    super(props);
    const songs = [];
    const preview_url = "";
    this.state = { songs, preview_url };

    this.getSpotify = this.getSpotify.bind(this);
    this.playSong = this.playSong.bind(this);
  }

  componentDidMount() {
    this.getSpotify();
  }

  getSpotify() {
    if (this.state.preview_url != "") {
      this.state.preview_url.play();
    }
    axios
      .get("/spotify-req", {
        params: {}
      })
      .then(res => {
        console.log(res.data.songOptions);
        console.log(res.data.answer);
        console.log("it called a thing");
        this.setState({
          songs: res.data.songOptions,
          preview_url: new Audio(
            res.data.songOptions[res.data.answer].preview_url
          )
        });
      })
      .catch(err => {});
  }

  playSong() {
    //var a = new Audio(this.state.preview_url);
    this.state.preview_url.play();
  }
  render() {
    return (
      <div>
        <button id="graph-go" onClick={this.getSpotify}>
          Next
        </button>
        <button id="graph-go" onClick={this.playSong}>
          Play Song
        </button>
        {this.state.songs.map((song, index) => (
          <p key={index}> {song.name} </p>
        ))}
      </div>
    );
  }
}
export default Jukebox;

