import React, { Component } from "react";
import axios from "axios";

import "../styles/jukebox.css";

class Jukebox extends Component {
  constructor() {
    super();
  }

  getSpotify() {
    axios
      .get("/get-article-title-and-url", {
        params: {
          conversation_id: decodeURI(conversation_id)
        }
      })
      .then(res => {
        this.setState({
          title: res.data[0].title,
          url: res.data[0].url
        });
      })
      .catch(err => {});
  }
  render() {
    return <div id="juke">Jukebox</div>;
  }
}

export default Jukebox;
