import React, { Component } from "react";
import axios from "axios";

import "../styles/jukebox.css";

const colors = ["#146ACF", "#448CDF", "#6199DA", "#80B4F0"];

const Jukebox = ({ dataItem = [], contentType }) => {
  if (contentType === "articles") {
    return (
      <section id="top-four-cards">
        {dataItem.slice(0, 4).map((card, index) => (
          <div key={index.toString()} className="col-md-3">
            <div className="card" style={{ backgroundColor: colors[index] }}>
              <div className="details">
                <div className="col-md-9">
                  <div className="top-details">
                    <span className="name">{card.name}</span>
                  </div>
                  <div className="bottom-details">
                    <div className="amount" />
                    <div />
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </section>
    );
  }

  var normalizedData = [];
  if (store.getState().homePageReducer.sortType === "watsonscore") {
    normalizedData = watsonNormalize(dataItem);
  }
  return (
    <section id="top-four-cards">
      {dataItem.slice(0, 4).map((card, index) => (
        <div key={index.toString()} className="col-md-3">
          <div className="card" style={{ backgroundColor: colors[index] }}>
            <Link
              to={"/tagpage/" + encodeURI(card.name)}
              style={{ textDecoration: "none", color: "white" }}
            >
              <div className="details">
                <div className="col-md-9">
                  <div className="top-details">
                    <span className="name">{card.name}</span>
                  </div>
                  <div className="bottom-details">
                    <div className="amount">
                      <span className="number">
                        {store.getState().homePageReducer.sortType ===
                        "watsonscore"
                          ? normalizedData[index]
                          : card.main_metric}
                      </span>
                    </div>
                    <div>
                      <span className="amount-type">
                        {getMetricType(
                          store.getState().homePageReducer.sortType
                        )}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          </div>
        </div>
      ))}
    </section>
  );
};

